pragma solidity ^0.8.0;

abstract contract IJADE {
  /**
   * @notice Event emitted when tokens are rebased
   */
  event Rebase(uint256 epoch, uint256 prevJadesScalingFactor, uint256 newJadesScalingFactor);

  /* - Extra Events - */
  /**
   * @notice Tokens minted event
   */
  event Mint(address to, uint256 amount);

  /**
   * @notice Tokens burned event
   */
  event Burn(address from, uint256 amount);

  /**
   * @notice InitTimestamp set (see rebase())
   */
  event StartBlockSet(uint256 value);
}

// File: contracts/Jade.sol

pragma solidity ^0.8.0;

contract JadeToken is ERC20PresetMinterRebaser, Ownable, IJADE {
  using SafeMath for uint256;

  /**
   * @dev Guard variable for re-entrancy checks. Not currently used
   */
  bool internal _notEntered;

  /**
   * @notice Internal decimals used to handle scaling factor
   */
  uint256 public constant internalDecimals = 10 ** 24;

  /**
   * @notice Used for percentage maths
   */
  uint256 public constant BASE = 10 ** 18;

  /**
   * @notice Scaling factor that adjusts everyone's balances
   */
  uint256 public jadesScalingFactor;

  mapping(address => uint256) internal _jadeBalances;

  mapping(address => mapping(address => uint256)) internal _allowedFragments;

  uint256 public initSupply;

  uint256 public initTimestamp;
  uint256 public startBlock;
  // Last block where rebase was called
  uint256 public lastBlock;
  // Rebase can be disabled 90 days after token launch
  bool public rebaseEnabled;
  // Core Bankroll rewards per block
  uint256 public JCBRewardPerBlock = 300e18;

  // keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");
  bytes32 public constant PERMIT_TYPEHASH =
    0x6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9;
  bytes32 public DOMAIN_SEPARATOR;

  mapping(address => uint256) public nonces;

  /// @notice The EIP-712 typehash for the contract's domain
  bytes32 public constant DOMAIN_TYPEHASH =
    keccak256("EIP712Domain(string name,uint256 chainId,address verifyingContract)");

  uint256 private INIT_SUPPLY = 888888889 * 10 ** 18;
  uint256 private _totalSupply;

  modifier validRecipient(address to) {
    require(to != address(0x0));
    require(to != address(this));
    _;
  }

  constructor() ERC20PresetMinterRebaser("Jade", "JADE") {
    jadesScalingFactor = BASE;
    initSupply = _fragmentToJade(INIT_SUPPLY);
    _totalSupply = INIT_SUPPLY;
    _jadeBalances[owner()] = initSupply;
    startBlock = 0;
    lastBlock = 0;
    rebaseEnabled = false;
    initTimestamp = block.timestamp;

    emit Transfer(address(0), msg.sender, INIT_SUPPLY);
  }

  /**
   * @return The total number of fragments.
   */
  function totalSupply() public view override returns (uint256) {
    return _totalSupply;
  }

  /**
   * @notice Computes the current max scaling factor
   */
  function maxScalingFactor() external view returns (uint256) {
    return _maxScalingFactor();
  }

  function _maxScalingFactor() internal view returns (uint256) {
    // scaling factor can only go up to 2**256-1 = initSupply * jadesScalingFactor
    // this is used to check if jadesScalingFactor will be too high to compute balances when rebasing.
    return uint256(int256(-1)) / initSupply;
  }

  /**
   * @notice Mints new tokens, increasing totalSupply, initSupply, and a users balance.
   */
  function mint(address to, uint256 amount) external returns (bool) {
    require(hasRole(MINTER_ROLE, _msgSender()), "Must have minter role");

    _mint(to, amount);
    return true;
  }

  function _mint(address to, uint256 amount) internal override {
    // increase totalSupply
    _totalSupply = _totalSupply.add(amount);

    // get underlying value
    uint256 jadeValue = _fragmentToJade(amount);

    // increase initSupply
    initSupply = initSupply.add(jadeValue);

    // make sure the mint didnt push maxScalingFactor too low
    require(jadesScalingFactor <= _maxScalingFactor(), "max scaling factor too low");

    // add balance
    _jadeBalances[to] = _jadeBalances[to].add(jadeValue);

    emit Mint(to, amount);
    emit Transfer(address(0), to, amount);
  }

  /**
   * @notice Burns tokens from msg.sender, decreases totalSupply, initSupply, and a users balance.
   */

  function burn(uint256 amount) public override {
    _burn(amount);
  }

  function _burn(uint256 amount) internal {
    // decrease totalSupply
    _totalSupply = _totalSupply.sub(amount);

    // get underlying value
    uint256 jadeValue = _fragmentToJade(amount);

    // decrease initSupply
    initSupply = initSupply.sub(jadeValue);

    // decrease balance
    _jadeBalances[msg.sender] = _jadeBalances[msg.sender].sub(jadeValue);
    emit Burn(msg.sender, amount);
    emit Transfer(msg.sender, address(0), amount);
  }

  /**
   * @notice Mints new tokens using underlying amount, increasing totalSupply, initSupply, and a users balance.
   */
  function mintUnderlying(address to, uint256 amount) public returns (bool) {
    require(hasRole(MINTER_ROLE, _msgSender()), "Must have minter role");

    _mintUnderlying(to, amount);
    return true;
  }

  function _mintUnderlying(address to, uint256 amount) internal {
    // increase initSupply
    initSupply = initSupply.add(amount);

    // get external value
    uint256 scaledAmount = _jadeToFragment(amount);

    // increase totalSupply
    _totalSupply = _totalSupply.add(scaledAmount);

    // make sure the mint didnt push maxScalingFactor too low
    require(jadesScalingFactor <= _maxScalingFactor(), "max scaling factor too low");

    // add balance
    _jadeBalances[to] = _jadeBalances[to].add(amount);

    emit Mint(to, scaledAmount);
    emit Transfer(address(0), to, scaledAmount);
  }

  /**
   * @dev Transfer underlying balance to a specified address.
   * @param to The address to transfer to.
   * @param value The amount to be transferred.
   * @return True on success, false otherwise.
   */
  function transferUnderlying(address to, uint256 value) public validRecipient(to) returns (bool) {
    // sub from balance of sender
    _jadeBalances[msg.sender] = _jadeBalances[msg.sender].sub(value);

    // add to balance of receiver
    _jadeBalances[to] = _jadeBalances[to].add(value);
    emit Transfer(msg.sender, to, _jadeToFragment(value));
    return true;
  }

  /* - ERC20 functionality - */

  /**
   * @dev Transfer tokens to a specified address.
   * @param to The address to transfer to.
   * @param value The amount to be transferred.
   * @return True on success, false otherwise.
   */
  function transfer(address to, uint256 value) public override validRecipient(to) returns (bool) {
    // underlying balance is stored in jades, so divide by current scaling factor

    // note, this means as scaling factor grows, dust will be untransferrable.
    // minimum transfer value == jadesScalingFactor / 1e24;

    // get amount in underlying
    uint256 jadeValue = _fragmentToJade(value);

    // sub from balance of sender
    _jadeBalances[msg.sender] = _jadeBalances[msg.sender].sub(jadeValue);

    // add to balance of receiver
    _jadeBalances[to] = _jadeBalances[to].add(jadeValue);
    emit Transfer(msg.sender, to, value);

    return true;
  }

  /**
   * @dev Transfer tokens from one address to another.
   * @param from The address you want to send tokens from.
   * @param to The address you want to transfer to.
   * @param value The amount of tokens to be transferred.
   */
  function transferFrom(
    address from,
    address to,
    uint256 value
  ) public override validRecipient(to) returns (bool) {
    // decrease allowance
    _allowedFragments[from][msg.sender] = _allowedFragments[from][msg.sender].sub(value);

    // get value in jades
    uint256 jadeValue = _fragmentToJade(value);

    // sub from from
    _jadeBalances[from] = _jadeBalances[from].sub(jadeValue);
    _jadeBalances[to] = _jadeBalances[to].add(jadeValue);
    emit Transfer(from, to, value);

    return true;
  }

  /**
   * @param who The address to query.
   * @return The balance of the specified address.
   */
  function balanceOf(address who) public view override returns (uint256) {
    return _jadeToFragment(_jadeBalances[who]);
  }

  /** @notice Currently returns the internal storage amount
   * @param who The address to query.
   * @return The underlying balance of the specified address.
   */
  function balanceOfUnderlying(address who) public view returns (uint256) {
    return _jadeBalances[who];
  }

  /**
   * @dev Function to check the amount of tokens that an owner has allowed to a spender.
   * @param owner_ The address which owns the funds.
   * @param spender The address which will spend the funds.
   * @return The number of tokens still available for the spender.
   */
  function allowance(address owner_, address spender) public view override returns (uint256) {
    return _allowedFragments[owner_][spender];
  }

  /**
   * @dev Approve the passed address to spend the specified amount of tokens on behalf of
   * msg.sender. This method is included for ERC20 compatibility.
   * increaseAllowance and decreaseAllowance should be used instead.
   * Changing an allowance with this method brings the risk that someone may transfer both
   * the old and the new allowance - if they are both greater than zero - if a transfer
   * transaction is mined before the later approve() call is mined.
   *
   * @param spender The address which will spend the funds.
   * @param value The amount of tokens to be spent.
   */
  function approve(address spender, uint256 value) public override returns (bool) {
    _allowedFragments[msg.sender][spender] = value;
    emit Approval(msg.sender, spender, value);
    return true;
  }

  /**
   * @dev Increase the amount of tokens that an owner has allowed to a spender.
   * This method should be used instead of approve() to avoid the double approval vulnerability
   * described above.
   * @param spender The address which will spend the funds.
   * @param addedValue The amount of tokens to increase the allowance by.
   */
  function increaseAllowance(address spender, uint256 addedValue) public override returns (bool) {
    _allowedFragments[msg.sender][spender] = _allowedFragments[msg.sender][spender].add(addedValue);
    emit Approval(msg.sender, spender, _allowedFragments[msg.sender][spender]);
    return true;
  }

  /**
   * @dev Decrease the amount of tokens that an owner has allowed to a spender.
   *
   * @param spender The address which will spend the funds.
   * @param subtractedValue The amount of tokens to decrease the allowance by.
   */
  function decreaseAllowance(
    address spender,
    uint256 subtractedValue
  ) public override returns (bool) {
    uint256 oldValue = _allowedFragments[msg.sender][spender];
    if (subtractedValue >= oldValue) {
      _allowedFragments[msg.sender][spender] = 0;
    } else {
      _allowedFragments[msg.sender][spender] = oldValue.sub(subtractedValue);
    }
    emit Approval(msg.sender, spender, _allowedFragments[msg.sender][spender]);
    return true;
  }

  // --- Approve by signature ---
  function permit(
    address owner,
    address spender,
    uint256 value,
    uint256 deadline,
    uint8 v,
    bytes32 r,
    bytes32 s
  ) public {
    require(block.timestamp <= deadline, "JADE/permit-expired");

    bytes32 digest = keccak256(
      abi.encodePacked(
        "\x19\x01",
        DOMAIN_SEPARATOR,
        keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, value, nonces[owner]++, deadline))
      )
    );

    require(owner != address(0), "JADE/invalid-address-0");
    require(owner == ecrecover(digest, v, r, s), "JADE/invalid-permit");
    _allowedFragments[owner][spender] = value;
    emit Approval(owner, spender, value);
  }

  function rebase(uint256 epoch, uint256 indexDelta, bool positive) public returns (uint256) {
    require(rebaseEnabled, "Rebase disabled");
    require(hasRole(REBASER_ROLE, _msgSender()), "Must have rebaser role");

    // no change
    if (indexDelta == 0) {
      emit Rebase(epoch, jadesScalingFactor, jadesScalingFactor);
      return _totalSupply;
    }

    // for events
    uint256 prevJadesScalingFactor = jadesScalingFactor;

    if (!positive) {
      // negative rebase, decrease scaling factor
      jadesScalingFactor = jadesScalingFactor.mul(BASE.sub(indexDelta)).div(BASE);
    } else {
      // positive rebase, increase scaling factor
      uint256 newScalingFactor = jadesScalingFactor.mul(BASE.add(indexDelta)).div(BASE);
      if (newScalingFactor < _maxScalingFactor()) {
        jadesScalingFactor = newScalingFactor;
      } else {
        jadesScalingFactor = _maxScalingFactor();
      }
    }

    // update total supply, correctly
    _totalSupply = _jadeToFragment(initSupply);

    emit Rebase(epoch, prevJadesScalingFactor, jadesScalingFactor);
    return _totalSupply;
  }

  function jadeToFragment(uint256 jade) public view returns (uint256) {
    return _jadeToFragment(jade);
  }

  function fragmentToJade(uint256 value) public view returns (uint256) {
    return _fragmentToJade(value);
  }

  function _jadeToFragment(uint256 jade) internal view returns (uint256) {
    return jade.mul(jadesScalingFactor).div(internalDecimals);
  }

  function _fragmentToJade(uint256 value) internal view returns (uint256) {
    return value.mul(internalDecimals).div(jadesScalingFactor);
  }

  // Rescue tokens
  function rescueTokens(address token, address to, uint256 amount) public onlyOwner returns (bool) {
    // transfer to
    SafeERC20.safeTransfer(IERC20(token), to, amount);
    return true;
  }

  function setLastBlock(uint256 _value) external {
    require(hasRole(REBASER_ROLE, _msgSender()), "Must have rebaser role");
    lastBlock = _value;
  }

  // Set rebaseEnabled 90 days after launch
  function setRebaseEnabled(bool _bool) external onlyOwner {
    if (rebaseEnabled) {
      require(block.timestamp > initTimestamp + 7776000, "Must wait 90 days to deactivate"); // 90 days
    }

    rebaseEnabled = _bool;
  }

  function setStartBlock(uint256 _value) external onlyOwner {
    require(startBlock == 0, "startBlock can only be set ONCE!!!");
    startBlock = _value;
    emit StartBlockSet(_value);
  }
}

